import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'


class SumIngredients extends React.Component {

    state = {
        data: []
    }

    componentDidMount(){
        axios.get('http://localhost:4000/sum')
        .then(res => {
      this.setState({
        data: res.data
      } )

          })

        }
        


    render() {
     //   console.log(req.params.id)
        console.log(this.state.data)
        console.log(typeof this.state.data)
        var ings = this.state.data
        console.log(Object.values(ings))
        // var vol = ings.map(filter)
        var romp = Object.values(ings)
        var vol = romp.filter(ingredient => ingredient._id.Type === 'tsp' || ingredient._id.Type === 'tbs');
        var oz = vol.forEach(function(){
            if(this.Type === 'tsp'){
                return this.Amount*3;
            } else if(this.Type === 'tbs'){
                return this.Amount*1;
            } else if(this.Type === 'floz'){
                return this.Amount*1;
            } else if (this.Type === 'cup'){
                return this.Amount*8;
            } else if (this.Type === 'ml'){
                return this.Amount*.033814;
            } else if (this.Type === 'L'){
                return this.Amount*.029;
            } 
        }
    );
        console.log(vol);
        console.log(oz)
        var data =
        romp.map((ins) => (
            <p>
            
               {/* <h3>ID: {ins._id}</h3> */}
               <h3>Recipe: {ins.Title}</h3>
               <h3>ID: {ins._id}</h3>
               <Link to={`/recipe/${ins._id}`}>See more Details</Link>
               {/* <h3>Ingredients: {ins.Ingredients}</h3>
               <h3>Directions: {ins.Directions}</h3> */}
            
            </p>
                 
                )
        )
            
            
        return (
            
                <table>
    <tr>
   <th>Ingredient:</th>
   <th>Amount:</th>
   <th>Type:</th>
   <th>FLOZ:</th>
   <th>Cup:</th>
   <th>tbs:</th>
   <th>tsp:</th>
   <th>mL:</th>
   <th>L: </th>
    </tr>
            {/* {data} */}
            </table>
        )
    }
}
    


export default SumIngredients;
