import React, { Component, Fragment } from 'react'
import { AppBar, Toolbar, IconButton, Menu, MenuItem, MenuList, Typography, Button } from '@material-ui/core'
import { Link } from 'react-router-dom';
import Router from '../Router';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../actions/auth';

// import LoggedIn from './LoggedIn';
// Import { Link } here //

const Navigation = ({ auth: { isAuthenticated, loading }, logout }) => {
        
    const authLinks = 
        <Fragment>
                <MenuItem>
            <Link to="/ingredients_list"><Button color="white">Ingredients List</Button></Link>
                </MenuItem>
                <MenuItem>
            <Link to="/recipes_list">Recipes List</Link>
                </MenuItem>
                <MenuItem>
            <Link to="/add_recipe">Add Recipe</Link>
                </MenuItem>
                <MenuItem>
            <Link to="/add_ingredients">Add Ingredients</Link>
                </MenuItem>
                <MenuItem>
            <a onClick={logout} href='#!'>
                <span className="hide-sm">Logout</span></a>
                </MenuItem>
                </Fragment>

        const guestLinks = (
            <div>
            {/* // <ul className = "nav-list"> */}
            <li className="nav-list-item">
            <Link to="/login">Login</Link>
        </li>
        <li className="nav-list-item">
            <Link to="/register">Register</Link>
        </li>
            {/* </ul> */}
            </div>

        )
        return (
            <React.Fragment>
<AppBar position="relative">
            <Toolbar>
                <Typography variant="title" color="inherit" style={{ flexGrow: "1" }}>
                   Kitchen Inventory
                </Typography>
            
                {/* <ul className="nav-list">
                    <li className="nav-list-item">
                        <Link to="/">Home</Link>
                    </li> */}
                       {!loading && (<Fragment>
                           {isAuthenticated ? authLinks : guestLinks}
                       </Fragment>)}
                {/* </ul> */}
              

            </Toolbar>
        </AppBar>
            </React.Fragment>

    )
        
    }

// Navigation.propTypes = {
//     logout: PropTypes.func.isRequired,
//     auth: PropTypes.func.isRequired
// }

const mapStateToProps = state => ({
    auth: state.auth

})

export default connect(mapStateToProps, {logout})(Navigation);
