import React, { useState, useEffect } from "react";
import Axios from "axios";
// import material ui components here //
// Container, Paper, Chip //



const Recipe = (props) => {

    const grep = props.match.params.id;
    // console.log(grep)

    const [hasError, setErrors] = useState(false);
    const [recipe, setRecipe] = useState({});

    useEffect(() => {
        async function fetchRecipe() {
          const res = await fetch(`http://localhost:4000/recipe/${grep}`);
          res
            .json()
            .then(res => setRecipe(res))
            .catch(err => setErrors(err));
        }
    
        fetchRecipe();
    });

    const handleUseRecipe = () => {
        let rec = recipe.toString
        let strIngredients = recipe.Ingredients;
        let arIngredients = strIngredients.split(", ");
        let strAmounts = recipe.Amounts;
        let arAmounts = strAmounts.split(", ");
        let strUnits = recipe.Units;
        let arUnits = strUnits.split(", ");
        console.log(arIngredients);
        console.log(arAmounts);
        console.log(arUnits);
        // For Each
        // Axios.post('localhost:4000/add_ingredient')
    }
    // console.log(name.Origin)
    // const name = cars.find(x => x.id == props.match.params.id).Name
    const log =             recipe.Ingredients
return (
    
    <div>
        {log}
      <span>{JSON.stringify(recipe)}</span>
      <button onClick={handleUseRecipe()}>Use Recipe</button>
    </div>
   
)

    }

export default Recipe
