import React from 'react';
import { FormControl } from '@material-ui/core';
import { InputLabel } from '@material-ui/core';
import { Input } from '@material-ui/core';
import { FormHelperText } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';

class AddIngredient extends React.Component {

    state = {
        title: '',
        ingredients: '',
        directions: ''
    }

    handleChangeIngredient = (event) => {
        this.setState({
            ingredient: event.target.value
        })
        console.log(this.state.ingredient)
    }

    handleChangeUnit = (event) => {
        this.setState({
            unit: event.target.value
        })
        console.log(this.state.unit)
    }

    handleChangeMeasure = (event) => {
        this.setState({
            measure: event.target.value
        })
        console.log(this.state)
    }

    handleSubmit = (event) => {
        event.preventDefault()
        let ingredient = {
            ingredient: this.state.title,
            unit: this.state.ingredients,
            measure: this.state.directions
        }

        axios.post('http://localhost:4000/add_recipe', ingredient)
        this.setState({
            ingredient: '',
            unit: '',
            measure: ''
        })

    }

    render() {
        return (
            <div>
                            <form
                            // method="POST" action='http://localhost:4000/add_recipe' 
                            onSubmit={this.handleSubmit}
                            >
            <input id="ingredient" name="ingredient" type="text" value={this.state.ingredient} onChange={this.handleChangeIngredient} />
            <br></br>
            <input id="measure" name="measure" type="text" value={this.state.measure} onChange={this.handleChangeMeasure} />
            <br></br>
            <input id="unit" name="unit" type="text" value={this.state.directions} onChange={this.handleChangeUnit} />
            <br></br>
            <input type='submit' value="Submit Ingredient"/>
          </form>
            </div>

        )

    }
}

export default AddIngredient;
