import { login } from '../actions/auth';


const Login = ({ login }) => {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    })


const { email, password } = formData;

const onChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
}
