import React from 'react';
import { FormControl } from '@material-ui/core';
import { InputLabel } from '@material-ui/core';
import { Input } from '@material-ui/core';
import { FormHelperText } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';

class AddIngredient extends React.Component {

    state = {
        ingredient: '',
        units: '',
        measure: ''
    }

    handleChangeIngredient = (event) => {
        this.setState({
            ingredient: event.target.value
        })
        console.log(this.state.ingredient)
    }

    handleChangeUnits = (event) => {
        this.setState({
            units: event.target.value
        })
        console.log(this.state.units)
    }

    handleChangeMeasure = (event) => {
        this.setState({
            measure: event.target.value
        })
        console.log(this.state.measure)
    }

    handleSubmit = (event) => {
        event.preventDefault()
        let post = {
            Ingredient: this.state.ingredient,
            Unit: this.state.units,
            Measure: this.state.measure
        }

        axios.post('http://localhost:4000/add_ingredient', post)
        this.setState({
            ingredient: '',
            units: '',
            measure: ''
        })

    }

    render() {
        return (
            <div>
                            <form 
                            // method="POST" action='http://localhost:4000/add_ingredient' 
                            onSubmit={this.handleSubmit}
                            >
            <input id="ingredient" name="ingredient" type="text" value={this.state.ingredient} onChange={this.handleChangeIngredient} />
            <br></br>
            <input id="unit" name="unit" type="text" value={this.state.units} onChange={this.handleChangeUnits} />
            <br></br>
            <input id="measure" name="measure" type="text" value={this.state.measure} onChange={this.handleChangeMeasure} />
            <br></br>
            <a href="http://localhost:3000/add_ingredient"><input type='submit' text="Next Ingredient"/></a>
            <a href="http://localhost:3000/Home"><input type='submit' text="Done"/></a>
          </form>
            </div>

        )

    }
}

export default AddIngredient;
