import React from 'react';
import axios from 'axios';


class IngredientsList extends React.Component {

    state = {
        data: []
    }

    componentDidMount() {
        this.getIngredients()
    }

    getIngredients = () => {
        axios.get('http://localhost:4000/')
        .then((res) => {
            const data  = res.data;
            this.setState({ data: data })
            console.log('Got data.');
        })
        .catch(() => {
            console.log('Error')
        });
    }

    displayIngredients = (ings) => {

      ings.map(ins => {
          <p>
                <h3>ins.Ingredient</h3>
                <h3>ins.Unit</h3>
                <h3>ins.Measure</h3>
          </p>

      
        })
    }

    render() {

    
        return (
            <div>
                {this.state.data.map( (da) => (
                    da.Ingredient
                ))}
            </div>
        );

    }
}

export default IngredientsList;
