import React from 'react';
import axios from 'axios';


class IngredientsList extends React.Component {

    state = {
        data: []
    }

    componentDidMount(){
        axios.get('http://localhost:4000/')
        .then(res => {
      this.setState({
        data: res.data
      } )

          })
      
        }
        

    displayIngredients = (ings) => {
        if(ings.length){

     ings.map((ins, index) => {
          <p key={index}>
                <h3>{ins.Ingredient}</h3>
                <h3>{ins.Unit}</h3>
                <h3>{ins.Measure}</h3>
          </p>

     
     })
    }
}

    render() {
        console.log(this.state)
        console.log(this.displayIngredients())
    
        return (
            <div>
              {  this.displayIngredients(this.state.data) }
            </div>
        );

    }
}

export default IngredientsList;
