import React from 'react';
import { FormControl } from '@material-ui/core';
import { InputLabel } from '@material-ui/core';
import { Input } from '@material-ui/core';
import { FormHelperText } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';

class AddIngredient extends React.Component {

    state = {
        ingredient: '',
        unit: '',
        measure: ''
    }

    handleChangeIngredients = (event) => {
        this.setState({
            ingredients: event.target.value
        })
        console.log(this.state.ingredients)
    }

    handleChangeRecipe = (event) => {
        this.setState({
            recipe: event.target.value
        })
        console.log(this.state.recipe)
    }

    handleChangeDirections = (event) => {
        this.setState({
            directions: event.target.value
        })
        console.log(this.state.directions)
    }

    handleSubmit = (event) => {
        event.preventDefault()
        let post = {
            Recipe: this.state.recipe,
            Ingredients: this.state.ingredients,
            Directions: this.state.directions
        }

        axios.post('http://localhost:4000/add_ingredient', post)
        this.setState({
            recipe: '',
            ingredient: '',
            directions: ''
        })

    }

    render() {
        return (
            <div>
                            <form 
                            // method="POST" action='http://localhost:4000/add_ingredient' 
                            onSubmit={this.handleSubmit}
                            >
            <input id="ingredient" name="ingredient" type="text" value={this.state.recipe} onChange={this.handleChangeIngredient} />
            <br></br>
            <input id="unit" name="unit" type="text" value={this.state.ingredients} onChange={this.handleChangeUnit} />
            <br></br>
            <input id="measure" name="measure" type="text" value={this.state.directions} onChange={this.handleChangeMeasure} />
            <br></br>
            <input type='submit' value="Next Ingredient"/>
          </form>
          <a href='/'>Done</a>
            </div>

        )

    }
}

export default AddIngredient;
