import React from 'react';
import { FormControl } from '@material-ui/core';
import { InputLabel } from '@material-ui/core';
import { Input } from '@material-ui/core';
import { FormHelperText } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';

class AddRecipe extends React.Component {

    state = {
        title: '',
        ingredients: '',
        amounts: '',
        types: '',
        directions: ''
    }

    handleChangeTitle = (event) => {
        this.setState({
            title: event.target.value
        })
        console.log(this.state.title)
    }

    handleChangeIngredients = (event) => {
        this.setState({
            ingredients: event.target.value
        })
        console.log(this.state.ingredients)
    }

    handleChangeAmounts = (event) => {
        this.setState({
            amounts: event.target.value
        })
        console.log(this.state.amounts)
    }

    handleChangeType = (event) => {
        this.setState({
            types: event.target.value
        })
        console.log(this.state.types)
    }

    handleChangeDirections = (event) => {
        this.setState({
            directions: event.target.value
        })
        console.log(this.state)
    }

    handleSubmit = (event) => {
        event.preventDefault()
        let post = {
            title: this.state.title,
            ingredients: this.state.ingredients,
            amounts: this.state.amounts,
            types: this.state.types,
            directions: this.state.directions
        }

        axios.post('http://localhost:4000/add_recipe', post)
        this.setState({
            title: '',
            ingredients: '',
            amounts: '',
            types: '',
            directions: ''
        })

    }

    render() {
        return (
            <div>
                <h3>Recipe</h3>
                <p>{this.state.title}</p>
                <h3>Ingredients and Measurement</h3>
                <p>{this.state.ingredients}</p>
                <p>{this.state.amounts}</p>
                <p>{this.state.types}</p>
                            <form
                            // method="POST" action='http://localhost:4000/add_recipe' 
                            onSubmit={this.handleSubmit}
                            >
            Recipe:
            <input id="title" name="title" type="text" value={this.state.title} onChange={this.handleChangeTitle} />
            <br></br>
            Ingredients:
            <input id="ingredients" name="ingredients" type="text" value={this.state.ingredients} onChange={this.handleChangeIngredients} />
            <br></br>
            Amount:
            <input id="amounts" name="amounts" type="text" value={this.state.amounts} onChange={this.handleChangeAmounts} />
            <br></br>
            Measurement Type:
            <input id="types" name="types" type="text" value={this.state.types} onChange={this.handleChangeType} />
            <br></br>
            Directions:
            <input id="directions" name="directions" type="text" value={this.state.directions} onChange={this.handleChangeDirections} />
            <br></br>
            <input type='submit' value="Submit Recipe"/>
          </form>
            </div>

        )

    }
}

export default AddRecipe;
