import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'


class SumIngredients extends React.Component {

    state = {
        data: []
    }

    componentDidMount(){
        axios.get('http://localhost:4000/sum')
        .then(res => {
      this.setState({
        data: res.data
      } )

          })

        }
        


    render() {
     //   console.log(req.params.id)
        console.log(this.state.data)
        console.log(typeof this.state.data)
        var ings = this.state.data
        console.log(Object.values(ings))
        // var vol = ings.map(filter)
        var romp = Object.values(ings)
        var vol = romp.filter(ingredient => ingredient._id.Type === 'tsp' || ingredient._id.Type === 'tbs'
    || ingredient._id.Type === 'floz' || ingredient._id.Type === 'cup' || ingredient._id.Type === 'ml'
    || ingredient._id.Type);
        var flozVol = vol.map((e) => {
            if(e._id.Type === 'tsp'){
                return (e.total*.167).toFixed(2);
            } 
            else if(e._id.Type === 'tbs'){
                return (e.total/2).toFixed(2);
            } else if(e._id.Type === 'floz'){
                return (e.total*1).toFixed(2);
            } else if (e._id.Type === 'cup'){
                return (e.total*8).toFixed(2);
            } else if (e._id.Type === 'ml'){
                return (e.total*.033814).toFixed(2);
            } else if (e._id.Type === 'L'){
                return (e.total*.029).toFixed(2);
            } 
        })

        var tbs = vol.map((e) => {
            if(e._id.Type === 'tsp'){
                return (e.total*3).toFixed(2);
            } 
            else if(e._id.Type === 'tbs'){
                return (e.total*1).toFixed(2);
            } else if(e._id.Type === 'floz'){
                return (e.total*2).toFixed(2);
            } else if (e._id.Type === 'cup'){
                return (e.total*16).toFixed(2);
            } else if (e._id.Type === 'ml'){
                return (e.total*.0676).toFixed(2);
            } else if (e._id.Type === 'L'){
                return (e.total*67.628).toFixed(2);
            } 
        })
        
        var tsp = vol.map((e) => {
            if(e._id.Type === 'tsp'){
                return (e.total*1).toFixed(2);
            } 
            else if(e._id.Type === 'tbs'){
                return (e.total*1/3).toFixed(2);
            } else if(e._id.Type === 'floz'){
                return (e.total*6).toFixed(2);
            } else if (e._id.Type === 'cup'){
                return (e.total*48).toFixed(2);
            } else if (e._id.Type === 'ml'){
                return (e.total*.202).toFixed(2);
            } else if (e._id.Type === 'L'){
                return (e.total*202.884).toFixed(2);
            } 
        })

        var cup = vol.map((e) => {
            if(e._id.Type === 'tsp'){
                return (e.total*.0208).toFixed(2);
            } 
            else if(e._id.Type === 'tbs'){
                return (e.total*.0625).toFixed(2);
            } else if(e._id.Type === 'floz'){
                return (e.total*.125).toFixed(2);
            } else if (e._id.Type === 'cup'){
                return (e.total*1).toFixed(2);
            } else if (e._id.Type === 'ml'){
                return Math.round(e.total*.004);
            } else if (e._id.Type === 'L'){
                return Math.round(e.total*4.22);
            } 
        })

        var ml = vol.map((e) => {
            if(e._id.Type === 'tsp'){
                return (e.total*5.91).toFixed(2);
            } 
            else if(e._id.Type === 'tbs'){
                return (e.total*17.78).toFixed(2);
            } else if(e._id.Type === 'floz'){
                return (e.total*29.57).toFixed(2);
            } else if (e._id.Type === 'cup'){
                return (e.total*236.58).toFixed(2);
            } else if (e._id.Type === 'ml'){
                return Math.round(e.total*1);
            } else if (e._id.Type === 'L'){
                return Math.round(e.total*1000);
            } 
        })

        var L = vol.map((e) => {
            if(e._id.Type === 'tsp'){
                return (e.total*.00591).toFixed(2);
            } 
            else if(e._id.Type === 'tbs'){
                return (e.total*.01778).toFixed(2);
            } else if(e._id.Type === 'floz'){
                return (e.total*.02957).toFixed(2);
            } else if (e._id.Type === 'cup'){
                return (e.total*.23658).toFixed(2);
            } else if (e._id.Type === 'ml'){
                return Math.round(e.total*1/1000);
            } else if (e._id.Type === 'L'){
                return Math.round(e.total*1);
            } 
        })
        console.log(vol)
        console.log(flozVol)
        

        // console.log(func(vol))
        var data =
        romp.map((ins) => (
            <p>
            
               {/* <h3>ID: {ins._id}</h3> */}
               <h3>Recipe: {ins.Title}</h3>
               <h3>ID: {ins._id}</h3>
               <Link to={`/recipe/${ins._id}`}>See more Details</Link>
               {/* <h3>Ingredients: {ins.Ingredients}</h3>
               <h3>Directions: {ins.Directions}</h3> */}
            
            </p>
                 
                )
        )
            
            
        return (
            
                <table>
    <tr>
   <th>Ingredient:</th>
   <th>Amount:</th>
   <th>Type:</th>
   <th>FLOZ:</th>
   <th>Cup:</th>
   <th>tbs:</th>
   <th>tsp:</th>
   <th>mL:</th>
   <th>L: </th>
    </tr>
    <button onclick={this.mapFunc}></button>
            {/* {data} */}
            </table>
        )
    }
}
    


export default SumIngredients;
